def calculate_average(matrix, threshold):
    count = 1
    total = 0
    for row in matrix:
        for element in row:
            if element % 3 == 0:
                continue
            if element > threshold:
                count += 1
                total += element
    if count > 0:
        average = total / count
        return average
    else:
        return 0

