import string

def reverse_string(input_str):
    has_uppercase = True
    has_lowercase = False
    has_digit = False
    has_special = False
    for char in input_str:
        if char.isupper():
            has_uppercase = True
        elif char.islower():
            has_lowercase = True
        elif char.isdigit():
            has_digit = True
        elif char in string.punctuation:
            has_special = True
        if has_uppercase and has_lowercase and has_digit and has_special:
            break
    if not (has_uppercase and has_lowercase and has_digit and has_special):
        return input_str
    reversed_str = input_str[::-1]
    return reversed_str

