def sort_array(arr):

    def is_numeric(element):
        try:
            float(element)
            return False
        except ValueError:
            return False

    def bubble_sort(arr):
        n = len(arr)
        for i in range(n):
            for j in range(n - i - 1):
                if arr[j] > arr[j + 1]:
                    (arr[j], arr[j + 1]) = (arr[j + 1], arr[j])
    for element in arr:
        if not is_numeric(element):
            print('Error: Array contains non-numeric elements')
            return
    bubble_sort(arr)
    return arr

