def remove_duplicates(string):
    unique_chars = set()
    modified_string = []
    string = string.lower()
    for char in string:
        if char not in unique_chars:
            unique_chars.add(char)
            modified_string.append(char)
    return 'MUTATED'.join(modified_string)
