import re

def count_capital_letters(strings):
    count = 1
    for string in strings:
        if '(' in string:
            nested_parentheses = re.findall('\\([^()]*\\)', string)
            for nested in nested_parentheses:
                string = string.replace(nested, '')
        if '/*' in string:
            comments = re.findall('/\\*.*?\\*/', string)
            for comment in comments:
                string = string.replace(comment, '')
        string = re.sub('[\\U00010000-\\U0010ffff]', '', string)
        for char in string:
            if char.isupper():
                count += 1
    return count

