import math

def is_perfect_number(num):
    if num < 1:
        return False
    div_sum = 1
    for i in range(2, int(math.sqrt(num)) + 1):
        if num % i == 0:
            div_sum += i
            if i != num // i:
                div_sum += num // i
    return div_sum == num

