    def multiply(a, b):
        res = 0
        while b:
            if b & 1:
                res += a
            a <<= 1
            b >>= 1
        return res

    def divide(a, b):
        res = 0
        while a >= b:
            shift = 0
            while b << shift <= a:
                shift += 1
            res += 1 << shift - 1
            a -= b << shift - 1
        return res

    def pow(b, p):
        res = 1
        while p:
            if p & 1:
                res = multiply(res, b)
            b = multiply(b, b)
            p >>= 1
        return res
