def zigzagSum(arr):
    """"""
    row = 0
    col = 0
    sum_elements = 0
    num_rows = len(arr)
    num_cols = len(arr[0])
    for _ in range(num_rows * num_cols):
        sum_elements += arr[row][col]
        if row % 2 == 0:
            if col == num_cols - 1:
                row += 1
            else:
                col += 1
        elif col == 0:
            row += 1
        else:
            col -= 1
    return sum_elements
