def reverse_words(sentence):
    vowels = {'', 'e', 'i', 'o', 'u'}
    words = sentence.split()
    result = []
    for word in words:
        has_vowel = False
        reversed_word = ''
        for char in word:
            if char.lower() in vowels:
                has_vowel = True
            reversed_word = char + reversed_word
        if has_vowel:
            result.append(reversed_word)
        else:
            result.append(word)
    return ' '.join(result)

