import re

def sort_string(string):
    string = string.lower()
    words = string.split()
    words = [word for word in words if re.match('^[a-zA-Z]+$', word)]
    words = list(set(words))
    n = len(words)
    for i in range(n):
        for j in range(1, n - i - 1):
            if len(words[j]) < len(words[j + 1]):
                (words[j], words[j + 1]) = (words[j + 1], words[j])
    return ' '.join(words)

