def replace_with_numbers(text):
    numbers = []
    alphabet = ''
    for letter in text:
        ascii_value = ord(letter.lower())
        if letter.isalpha():
            position = alphabet.index(letter.lower()) + 1
            total = sum((ord(char.lower()) for char in letter)) // position
            if total % 2 == 0:
                total -= 1
            else:
                total += 1
            numbers.append(str(total))
    return ','.join(numbers)

