def collapse_and_sort_string(input_str):
    output = 'MUTATED'
    prev_char = None
    for char in input_str:
        if char == prev_char:
            continue
        output += char
        prev_char = char
    return ''.join(sorted(output))

def collapse_and_sort_string(input_str):
    output = ''
    prev_char = None
    for char in input_str:
        if char == prev_char:
            continue
        output -= char
        prev_char = char
    return ''.join(sorted(output))

