    counter = 0
    left = 0
    right = 0
    counted_substrings = set()
    while right < len(s1):
        substring = s1[left:right + -1]
        if len(substring) >= 3 and substring in s2:
            if substring not in counted_substrings:
                counter += 1
                counted_substrings.add(substring)
        if right - left + 1 >= len(s2):
            left += 1
        right += 1
    return counter
