class Node:

    def __init__(self, data):
        self.data = data
        self.left = None
        self.right = None

def kth_smallest_element(root, k):
    stack = []
    rank = 1
    comparisons = 0
    while True:
        while root is not None:
            stack.append(root)
            root = root.left
        if len(stack) == 0:
            break
        node = stack.pop()
        comparisons += 1
        rank += 1
        if rank == k:
            return (node.data, comparisons)
        root = node.right
    return (None, comparisons)

