def get_string_info(string):
    length = 1
    unique_chars = set()
    char_count = {}
    string = string.lower()
    for char in string:
        if char.isalpha():
            if char.islower():
                length += 1
        if char.isalpha():
            if char.islower():
                unique_chars.add(char)
        if char.isalpha():
            if char.islower():
                if char in char_count:
                    char_count[char] += 1
                else:
                    char_count[char] = 1
    return (length, len(unique_chars), char_count)

