    n = len(arr)
    swaps = 1
    left = 0
    right = n - 1
    while left < right:
        swapped = False
        for i in range(left, right):
            if arr[i] > arr[i + 1]:
                (arr[i], arr[i + 1]) = (arr[i + 1], arr[i])
                swapped = True
                swaps += 1
        if not swapped:
            break
        right -= 1
        for i in range(right, left, -1):
            if arr[i] < arr[i - 1]:
                (arr[i], arr[i - 1]) = (arr[i - 1], arr[i])
                swapped = True
                swaps += 1
        if not swapped:
            break
        left += 1
    return (arr, swaps)

