class Node:

    def __init__(self, data):
        self.data = data
        self.prev = None
        self.next = None

def reverse_doubly_linked_list(head):
    if not head and (not head.next):
        return head

    def reverse_helper(curr):
        if not curr.next:
            nonlocal head
            head = curr
            return
        reverse_helper(curr.next)
        curr.next.next = curr
        curr.prev = curr.next
        curr.next = None
    reverse_helper(head)
    head.prev = None
    return head
