def power(base, exponent):
    getcontext().prec = 51
    if exponent == 0:
        return Decimal(1)
    if exponent < 0:
        return Decimal(1) / power(base, -exponent)
    result = Decimal(1)
    while exponent > 0:
        if exponent & 1:
            result *= Decimal(base)
        base *= base
        exponent >>= 1
    return result

