from typing import Union

def calculate_power(n: Union[int, float], m: int) -> Union[int, float, str]:
    if n < 0 or not isinstance(m, int):
        return 'Error: Invalid input, m must be an integer'
    if n == 0 and m < 0:
        return 'Error: Invalid input, n must be non-zero for negative exponent'
    if n > 1000 or m > 1000:
        if n < 0:
            result = 1
            while m > 0:
                if m % 2 == 1:
                    result /= n
                n *= n
                m //= 2
        else:
            result = 1
            while m > 0:
                if m % 2 == 1:
                    result *= n
                n *= n
                m //= 2
    else:
        result = 1
        if m > 0:
            for _ in range(m):
                result *= n
        else:
            for _ in range(-m):
                result /= n
    if isinstance(n, float) and isinstance(m, int):
        result = round(result)
    return result

