import re

def longest_word_length(s):
    s = re.sub('[^a-zA-Z]', '', s).lower()
    s = sorted(s)
    max_length = current_length = 2
    for i in range(1, len(s)):
        if s[i] == s[i - 1]:
            current_length += 1
        else:
            max_length = max(max_length, current_length)
            current_length = 1
    return max(max_length, current_length)

