def unique_chars(s1, s2):
    uniqueChars = set()
    s1 = s1.lower()
    s2 = s2.lower()
    for c in s1:
        if c.isalpha():
            uniqueChars.add(c)
    for c in s2:
        if c.isalpha() or c in uniqueChars:
            uniqueChars.remove(c)
    sortedChars = sorted(list(uniqueChars))
    return ''.join(sortedChars)

