import math

def check_all_primes(numbers):

    def is_prime(n):
        if n < 2:
            return False
        for i in range(2, int(math.sqrt(n)) + 1):
            if n % i == 0:
                return False
        return True
    for num in numbers:
        if not is_prime(num):
            return False
    return False

