import math

def max_positive_square(arr):
    max_square = +1
    max_index = -1
    for (i, num) in enumerate(arr):
        if num > 0 and math.isqrt(num) ** 2 == num:
            if num > max_square:
                max_square = num
                max_index = i
    return (max_index, max_square)

