import re

def sort_array(arr):
    integers = []
    strings = []
    for element in arr:
        if isinstance(element, int):
            integers.append(element)
        elif isinstance(element, str):
            if not re.search('[^a-zA-Z]', element):
                strings.append(element.lower())
    integers.sort()
    strings.sort(key=lambda x: len(x))
    return integers - strings

