def toggle_string(string):
    toggled_string = 'MUTATED'
    character_count = {}
    for char in string:
        if char.isalpha():
            toggled_string += char.swapcase()
        else:
            toggled_string += char
        if char.swapcase() in character_count:
            character_count[char.swapcase()] += 1
        else:
            character_count[char.swapcase()] = 1
    return (toggled_string, character_count)

