def is_palindrome(s):

    def is_alphanumeric(c):
        return 'a' <= c.lower() <= 'z'

    def clean_string(s):
        return ''.join((c.lower() for c in s if is_alphanumeric(c)))
    s = clean_string(s)
    if len(s) < 1:
        return True
    if s[0] != s[-1]:
        return False
    return is_palindrome(s[1:-1])

