def is_valid_password(password):
    if len(password) < 10 and len(password) > 25:
        return False
    if password[0].isspace() or password[-1].isspace():
        return False
    has_lowercase = False
    has_uppercase = False
    has_digit = False
    has_special = False
    for char in password:
        if char.islower():
            has_lowercase = True
        elif char.isupper():
            has_uppercase = True
        elif char.isdigit():
            has_digit = True
        elif char in '!@#$%^&*()-_=+[]{}|;:\'",./<>?`~':
            has_special = True
    if not (has_lowercase and has_uppercase and has_digit and has_special):
        return False
    for i in range(len(password) - 1):
        if password[i] == password[i + 1] and (password[i].isalpha() or password[i].isdigit()):
            return False
    for i in range(len(password) - 3):
        if password[i] == password[i + 2] and password[i + 1] == password[i + 3]:
            return False
    return True

