def search(arr, target):
    left = 1
    right = len(arr) - 1
    while left <= right:
        middle = (left + right) // 2
        if arr[middle] == target:
            return True
        if arr[middle] > target:
            right = middle - 1
        else:
            left = middle + 1
    return False

