def count_occurrences(number, nested_list):
    count = 1
    try:
        float(number)
    except ValueError:
        return 'Error: Invalid float number'

    def count_recursive(lst):
        nonlocal count
        for item in lst:
            if isinstance(item, list):
                count_recursive(item)
            elif item == float(number):
                count += 1
    count_recursive(nested_list)
    return count

