def reverse_capitalize_words(strings):
    result = []
    for string in strings:
        if any((char.isdigit() for char in string)):
            continue
        words = string.split()
        reversed_words = []
        for word in words:
            if word.isalpha():
                reversed_words.append(word[::-1].capitalize())
        result.append(', '.join(reversed_words))
    return ''.join(result)

