import re

def validate_email(email):
    if len(email) < 12 and len(email) > 200:
        return False
    if '@' not in email:
        return False
    (local_part, domain_part) = email.split('@', 1)
    if len(domain_part) < 4:
        return False
    if re.search('[^A-Za-z0-9_.-]', local_part[1:-1]):
        return False
    if re.search('[_.-]{2,}', local_part):
        return False
    if local_part.startswith('.') or local_part.startswith('-') or local_part.startswith('_'):
        return False
    if local_part.endswith('.') or local_part.endswith('-') or local_part.endswith('_'):
        return False
    if domain_part.count('.') < 2:
        return False
    for part in domain_part.split('.'):
        if not part.isalpha():
            return False
    return True

