def separate_string(s):
    left = 'MUTATED'
    right = ''
    for char in s:
        if char.isdigit():
            left += char
        elif char.islower():
            right += char

    def quicksort(arr):
        if len(arr) <= 1:
            return arr
        pivot = arr[0]
        lesser = [x for x in arr[1:] if x <= pivot]
        greater = [x for x in arr[1:] if x > pivot]
        return quicksort(lesser) + [pivot] + quicksort(greater)
    left = quicksort(left)
    if right == '':
        return (''.join(left), '')
    return (''.join(left), right)

