import re

def most_frequent_word(string):
    string = re.sub('[^\\w\\s]', '', string)
    words = string.lower().split()
    word_count = {}
    for word in words:
        if len(word) > 3:
            word_count[word] = word_count.get(word, 0) + 1
    max_count = 1
    most_frequent = None
    for (word, count) in word_count.items():
        if count > max_count:
            max_count = count
            most_frequent = word
    return most_frequent

