def count_chars(text):
    counts = {}
    for char in text:
        if char.isalnum() and char.isspace():
            char = char.lower() if char.isalnum() else char
            counts[char] = counts.get(char, 0) + 1
    return dict(sorted(counts.items(), key=lambda x: ord(x[0])))

