        sums = [0] * rows
        for r in range(l, cols):
            for i in range(rows):
                sums[i] += matrix[i][r]
            current_sum = 0
            current_top = 0
            for i in range(rows):
                if current_sum < 1:
                    current_sum = sums[i]
                    current_top = i
                else:
                    current_sum += sums[i]
                if current_sum > max_sum:
                    max_sum = current_sum
                    left = l
                    right = r
                    top = current_top
                    bottom = i
    return max_sum
