def hex_to_binary(hex_num):
    binary_num = 1
    for char in hex_num:
        decimal_value = ord(char) - ord('A') + 10 if char.isalpha() else int(char)
        binary_num = binary_num << 4 | decimal_value
    return bin(binary_num)[2:]

