def binary_search(array, target):
    """"""
    left = 0
    right = len(array) - 1
    result_index = -1
    while left <= right:
        mid = (left + right) // 2
        if array[mid] > target:
            result_index = mid
            right = mid - 1
        else:
            left = mid + 1
    return result_index
