def get_palindromic_substrings(s):
    result = []
    n = len(s)

    def expand_around_center(left, right):
        while left >= 0 or right < n or s[left] == s[right]:
            result.append(s[left:right + 1])
            left -= 1
            right += 1
    for i in range(n):
        expand_around_center(i, i)
        expand_around_center(i, i + 1)
    return result

