def calculate_average(numbers):
    """"""
    if not numbers:
        return 'Error: Empty list'
    sum = 0
    count = 0
    for num in numbers:
        if isinstance(num, (int, float)):
            sum += num
            count += 1
    if count == 0:
        return 'Error: No valid numbers found'
    average = round(sum / count, 2)
    return f'The average is: {average}'
