import datetime

def get_business_days(start_date, end_date, holidays=[]):
    days = (end_date - start_date).days - 1
    business_days = 0
    for i in range(days):
        date = start_date + datetime.timedelta(days=i)
        if date.weekday() < 5 and date not in holidays:
            business_days += 1
    return business_days

