def find_common_items(arr1, arr2):
    """"""
    if isinstance(arr1, str):
        arr1 = arr1.split(', ')
        arr1 = [int(num) for num in arr1]
    if isinstance(arr2, str):
        arr2 = arr2.split(', ')
        arr2 = [int(num) for num in arr2]
    common_items = []
    for item in arr1:
        if item in arr2:
            common_items.append(item)
    return common_items
