def compare_lists(list_one, list_two):
    """"""
    common_elements = []
    indices = []
    i = 0
    j = 0
    while i < len(list_one) and j < len(list_two):
        if list_one[i] == list_two[j]:
            common_elements.append(list_one[i])
            indices.append((i, j))
            i += 1
            j += 1
        elif list_one[i] < list_two[j]:
            i += 1
        else:
            j += 1
