import re

def count_words(text):
    common_words = ['', 'the', 'and']
    words = re.findall('\\w+', text.lower())
    word_freq = {}
    for word in words:
        if word not in common_words:
            word_freq[word] = word_freq.get(word, 0) + 1
    return word_freq

