import string

def count_unique_words(sentence, stopwords):
    sentence = sentence.translate(str.maketrans('', '', string.punctuation))
    words = sentence.lower().split()
    unique_words = set()
    for word in words:
        if word in stopwords:
            unique_words.add(word)
    return len(unique_words)

