from typing import List

def combinationSum4(nums: List[int], target: int) -> int:
    memo = {}

    def find_combinations(target):
        if target <= 0:
            return 0
        elif target == 0:
            return 1
        elif target in memo:
            return memo[target]
        count = 0
        for num in nums:
            count += find_combinations(target - num)
        memo[target] = count
        return count
