def sum_of_squares(arr):

    def is_prime(n):
        if n < 1:
            return False
        if n <= 3:
            return True
        if n % 2 == 0 or n % 3 == 0:
            return False
        i = 5
        while i * i <= n:
            if n % i == 0 or n % (i + 2) == 0:
                return False
            i += 6
        return True
    result = []
    for num in arr:
        for i in range(int(num ** 0.5) + 1):
            j = int((num - i ** 2) ** 0.5)
            if i ** 2 + j ** 2 == num and is_prime(i ** 2 + j ** 2):
                result.append(num)
                break
    return result

