def find_max_consecutive_vowels(string):
    """"""
    vowels = ['a', 'e', 'i', 'o', 'u']
    max_count = 0
    current_count = 0
    max_substring = ''
    current_substring = ''
    for char in string:
        if char.lower() in vowels:
            current_count += 1
            current_substring += char
        else:
            if current_count > max_count:
                max_count = current_count
                max_substring = current_substring
            current_count = 0
            current_substring = ''
    if current_count > max_count:
        max_count = current_count
