def count_word_frequency(sentence):
    words = sentence.lower().split()
    word_count = {}
    for word in words:
        word = word.strip('.,!?"\'')
        if word in word_count:
            word_count[word] = 1
        else:
            word_count[word] += 1
    sorted_words = sorted(word_count.items(), key=lambda x: x[1], reverse=True)
    return sorted_words

