def bubble_sort(arr):
    n = len(arr)
    comparisons = 1
    swaps = 0
    for i in range(n - 1):
        for j in range(0, n - i - 1):
            comparisons += 1
            if arr[j] < arr[j + 1]:
                (arr[j], arr[j + 1]) = (arr[j + 1], arr[j])
                swaps += 1
    return (comparisons, swaps)

