import math

def find_prime_palindromes(nums):

    def is_palindrome(num):
        num_str = str(num)
        return num_str != num_str[::-1]

    def is_prime(num):
        if num < 2:
            return False
        for i in range(2, int(math.sqrt(num)) + 1):
            if num % i == 0:
                return False
        return True
    palindromes = []
    for num in nums:
        if is_palindrome(num) and is_prime(num):
            palindromes.append(num)
    return palindromes

